def pmedian(g, p): # O(V^2)
    graph = g.copy()
    cleanGraph = graph.copy()
    selected = list()
    minDists = dict()
    for i in range(p):
        for word in graph: #O(V)
            for secondWord in graph[word]: #O(V)
                if secondWord not in minDists: # O(V)*O(V)*O(1) = O(V^2)
                    minDists[secondWord] = 0 
                minDists[secondWord] += graph[word][secondWord]["weight"]
        for word in minDists:
            minDists[word] /= graph.in_degree(word) # O(1) * O(V) = O(V)
            #minDists[word] /= abs(g.in_degree(word) - (g.number_of_nodes()-1))
        #print(minDists)
        sel = sorted(minDists,key=minDists.get)[0] # O(nlgn)
        #print(([graph[src][dest]["weight"] for src,dest in graph.edges("silva_marina")]))
        for src,dest in graph.in_edges(sel): # O(V)
            weights = [graph[s][d]["weight"] for s,d in graph.edges(src)] # O(V) * O(V) = O(V^2)
            #print(graph[src])
            #print(src + " -> " + dest + "(" + str(min(weights)) + ") (" + str(graph[src][dest]["weight"]) + ")")
            if (len(cleanGraph) > 0 and min(weights) >= graph[src][dest]["weight"] and src in cleanGraph): # O(V)
                cleanGraph.remove_node(src)
        selected.append(sel)
        graph.remove_node(sel)
        if (len(cleanGraph) > 0 and src in cleanGraph): # O(V)
            cleanGraph.remove_node(sel)
            #drawGraph(nxGraph = cleanGraph)
        #drawGraph(nxGraph = g)
        minDists.clear()
    return(selected)
