import numpy as np # linear algebra

import pandas as pd # data processing, CSV file I/O (e.g. pd.read_csv)

import matplotlib.pyplot as plt

import seaborn as sns

from sklearn import feature_extraction, linear_model, model_selection, preprocessing

import plotly.graph_objs as go

import plotly.offline as py



# Input data files are available in the "../input/" directory.

# For example, running this (by clicking run or pressing Shift+Enter) will list all files under the input directory



import os

for dirname, _, filenames in os.walk('/kaggle/input'):

    for filename in filenames:

        print(os.path.join(dirname, filename))



# Any results you write to the current directory are saved as output.
df = pd.read_csv('../input/mortality-rate-lungdisease-by-country-2020/Deaths_dueto_LungDisease_by_Country.csv', encoding='ISO-8859-2')

df.head()
import plotly.express as px

fig = px.bar(df[['Country', 'LungDisease']].sort_values('LungDisease', ascending=False), 

             y="LungDisease", x="Country", color='Country', 

             log_y=True, template='ggplot2', title='Lung Disease by Countries')

fig.show()
sns.lineplot(x='LungDisease',y='Influenza_Pneumonia',hue='Asthema',data=df)
sns.pairplot(data=df)
plt.figure(figsize=(50, 10))

g=sns.barplot(x='Country',y='Influenza_Pneumonia',data=df)
sns.distplot(df.Influenza_Pneumonia)
sns.distplot(df.LungDisease)
sns.scatterplot(x="Country", y="Influenza_Pneumonia",

                      hue="Asthema",

                      data=df)
plt.style.use('dark_background')

df.corr()

plt.figure(figsize=(10,4))

sns.heatmap(df.corr(),annot=True,cmap='summer')

plt.show()
plt.style.use('dark_background')

sns.jointplot(df['LungDisease'],df['Asthema'],data=df,kind='scatter')
total_Influenza_Pneumonia=df.groupby('Country')['Asthema','Country','Influenza_Pneumonia','LungDisease'].sum().reset_index()

total_Influenza_Pneumonia
fig=go.Figure(data=[

    go.Scatter(x=total_Influenza_Pneumonia['Country'],

    y=total_Influenza_Pneumonia['Influenza_Pneumonia'],marker_color='pink')]

)

fig.update_layout(title='Pneumonia by Country',font_size=16,yaxis_type='log',template='plotly_dark')

fig.show()
