df = pd.read_csv('/kaggle/input/covid19-research-preprint-data/COVID-19-Preprint-Data.csv')

df.head()
df["Number of Authors"].plot.hist()

plt.show()
df['Number of Authors'].hist(bins=50)

plt.title('Number of Authors')

plt.show()
sns.barplot(x=df['Number of Authors'].value_counts().index,y=df['Number of Authors'].value_counts())
keyword = 'adhesion'
def word_finder(i_word, i_text):

    found = str(i_text.lower()).find(i_word.lower())

    if found == -1:

        result = 0

    else:

        result = 1

    return result



# partial function for mapping

word_indicator_partial = lambda text: word_finder(keyword, text)

# build indicator vector (0/1) of hits

keyword_indicator = np.asarray(list(map(word_indicator_partial, df.Abstract)))
# number of hits

print('Number of hits for keyword <', keyword, '> : ', keyword_indicator.sum())
# add index vector as additional column

df['selection'] = keyword_indicator



# select only hits from data frame

df_hits = df[df['selection']==1]
# show results

df_hits
# look at an example: title...

df_hits['Title of preprint'].iloc[0]
# ... and abstract

df_hits.Abstract.iloc[0]
fig = px.bar(df, x= "Number of Authors", y= "Authors", color_discrete_sequence=['crimson'],)

fig.show()
fig = px.line(df, x="Number of Authors", y="Authors", color_discrete_sequence=['green'], 

              title="Covid-19 Preprint Data")

fig.show()
fig = px.scatter(df, x="Number of Authors", y="Authors", color="Uploaded Site",

                 color_continuous_scale=["red", "green", "blue"])



fig.show()
fig = px.parallel_coordinates(df, color="Number of Authors",

                             color_continuous_scale=[(0.00, "red"),   (0.33, "red"),

                                                     (0.33, "green"), (0.66, "green"),

                                                     (0.66, "blue"),  (1.00, "blue")])

fig.show()
fig = px.pie(df, values=df['Number of Authors'], names=df['Authors'],

             title='Covid-19 Preprint Data',

            )

fig.update_traces(textposition='inside', textinfo='percent+label')

fig.show()
fig = px.scatter_matrix(df)

fig.show()
