def plot_correlation_map( df ):

    corr = df.corr()

    _ , ax = plt.subplots( figsize =( 8 , 6 ) )

    cmap = sns.diverging_palette( 220 , 10 , as_cmap = True )

    _ = sns.heatmap(

        corr, 

        cmap = cmap,

        square=True, 

        cbar_kws={ 'shrink' : .9 }, 

        ax=ax, 

        annot = True, 

        annot_kws = { 'fontsize' : 12 }

    )



plot_correlation_map(df) 
